react.code:
import React, { useState } from 'react';

const VerifixAPI = {
    baseURL: 'https://verifix-otp.com/api',
    apiKey: 'api_key_example',

    async request(endpoint, data = null) {
        const options = {
            method: data ? 'POST' : 'GET',
            headers: {
                'Content-Type': 'application/json',
                'X-API-Key': this.apiKey
            }
        };

        if (data) {
            options.body = JSON.stringify(data);
        }

        const response = await fetch(`${this.baseURL}/${endpoint}`, options);
        return await response.json();
    },

    async sendVerification(email) {
        return await this.request('send-verification', { email });
    },

    async getBalance() {
        return await this.request('balance');
    }
};

// مكون React للتحقق
function VerificationComponent() {
    const [email, setEmail] = useState('');
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const result = await VerifixAPI.sendVerification(email);

            if (result.success) {
                setMessage('تم إرسال رسالة التحقق بنجاح');
            } else {
                setMessage('خطأ: ' + result.error);
            }
        } catch (error) {
            setMessage('خطأ في الاتصال بالخادم');
        }

        setLoading(false);
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="email" 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="البريد الإلكتروني"
                required 
            />
            <button type="submit" disabled={loading}>
                {loading ? 'جاري الإرسال...' : 'إرسال رسالة التحقق'}
            </button>
            {message && <p>{message}</p>}
        </form>
    );
}

export default VerificationComponent;
